                            "application/problem+json": {
                                "schema": {
                                    "$ref": "#/components/schemas/500"
                                }
                            }
                        }
                    }
                }
            }
        }
    },
    "components": {
        "parameters": {
            "userId": {
                "name": "id",
                "in": "query",
                "description": "ID of the user",
                "required": true,
                "schema": {
                    "$ref": "#/components/schemas/userId"
                }
            }
        },
        "schemas": {
            "User": {
                "type": "object",
                "required": [
                    "id",
                    "name"
                ],
                "properties": {
                    "id": {
                        "type": "integer",
                        "format": "int64"
                    },
                    "name": {
                        "type": "string"
                    },
                    "tag": {
                        "type": "string"
                    }
                }
            },
            "userId": {
                "type": "integer",
                "format": "int32"
            },
            "500": {
                "type": "object",
