                        "type": "string",
                        "example": "An error occurred.",
                        "description": "Details about the error."
                    },
                    "status": {
                        "type": "number",
                        "example": 500,
                        "description": "The HTTP status code."
                    }
                }
            },
            "Error": {
                "type": "object",
                "required": [
                    "code",
                    "message"
                ],
                "properties": {
                    "code": {
                        "type": "integer",
                        "format": "int32"
                    },
                    "message": {
                        "type": "string"
                    }
                }
            }
        },
        "securitySchemes": {
            "BasicAuth": {
                "type": "http",
                "scheme": ""
            }
        }
    },
